INSERT INTO `tbl_position` VALUES ('0e7bdbdf-0b77-435b-b9df-4e968ece4837', 'longkubi123', 'MaCV0014', 'longkubi', '2023-01-21 00:46:16.409000', '2022-11-29 11:47:41.921000', '- Trong quá trình phát triển web Backend Developer chịu trách nhiệm xây dựng và duy trì công nghệ mà dựa vào đó phần giao diện người dùng có thể tồn tại', 'Backend Developer');
