DROP FUNCTION IF EXISTS `int_to_string`;
delimiter ;;
CREATE FUNCTION `int_to_string`(STATUS INT)
 RETURNS varchar(255) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci
  DETERMINISTIC
BEGIN
    DECLARE result VARCHAR(255);
    CASE STATUS
        WHEN 4 THEN SET result = 'Đang Thực Hiện';
        WHEN 5 THEN SET result = 'Đã Kết Thúc';
        ELSE SET result = 'Không Hợp Lệ';
    END CASE;
    RETURN result;
END
;;
